using System.Collections; using System.Collections.Generic; using UnityEngine;  public class Boss {         	private int hp = 100;   // 体力 	private int power = 25; // 攻撃力 	private int mp = 53; 	// 攻撃用の関数 	public void Attack() {  		Debug.Log( this.power + "のダメージを与えた" ); 	}  	// 魔法攻撃用の関数 	public void Magic() { 		if (this.mp >= 5) { 			this.mp -= 5; 			Debug.Log ("魔法攻撃をした。残りMPは" + this.mp + "。"); 		}  else { 			Debug.Log ("MPが足りないため魔法が使えない。"); 		} 	} 	// 防御用の関数 	public  void Defence(int damage) {  		Debug.Log( damage+"のダメージを受けた" ); 		// 残りhpを減らす 		this.hp -= damage; 	}  }  public class Test2 : MonoBehaviour {  	void Start () { 		// Bossクラスの変数を宣言してインスタンスを代入 		Boss lastboss = new Boss();  		//10+1回戦闘 		for (int k = 0; k < 11; k++) { 			// 攻撃用の関数を呼び出す 			lastboss.Attack (); 			//魔法攻撃用の関数を呼び出す 			lastboss.Magic (); 			// 防御用の関数を呼び出す 			lastboss.Defence (3); 		} 	}  	// Update is called once per frame 	void Update () {  	} }